DROP TABLE IF EXISTS fangbao_admin
CREATE TABLE `fangbao_admin` (  `ID` int(6) NOT NULL AUTO_INCREMENT,  `Admin` varchar(100) NOT NULL,  `PassWord` varchar(100) NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_info
CREATE TABLE `fangbao_info` (  `ID` int(2) NOT NULL AUTO_INCREMENT,  `WebSiteName` varchar(100) DEFAULT NULL,  `WebSiteUrl` varchar(100) DEFAULT NULL,  `WebSiteTCP` varchar(50) DEFAULT NULL,  `WebTongJi` varchar(255) DEFAULT NULL,  `WebSiteKeyword` varchar(255) DEFAULT NULL,  `WebSiteIntr` varchar(255) DEFAULT NULL,  `WebSiteCopyInfo` varchar(255) DEFAULT NULL,  `Message` int(2) DEFAULT NULL,  `Powered` varchar(100) DEFAULT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_log
CREATE TABLE `fangbao_log` (  `LogID` int(12) NOT NULL AUTO_INCREMENT,  `LogIP` varchar(20) NOT NULL,  `LogTime` date NOT NULL,  `LogInfo` text NOT NULL,  `LogType` int(12) NOT NULL,  PRIMARY KEY (`LogID`)) ENGINE=InnoDB AUTO_INCREMENT=297 DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_news
CREATE TABLE `fangbao_news` (  `ID` int(12) NOT NULL AUTO_INCREMENT,  `Title` varchar(255) NOT NULL,  `Content` longtext NOT NULL,  `PublishDate` date NOT NULL,  `VerOrder` varchar(100) NOT NULL,  `VerOrderID` int(12) NOT NULL,  `Come` varchar(100) NOT NULL,  `User` varchar(100) NOT NULL,  `InfoTime` date NOT NULL,  `Hits` int(12) NOT NULL DEFAULT '100',  `sub_title` varchar(250) NOT NULL COMMENT '副标题',  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_paper
CREATE TABLE `fangbao_paper` (  `PublishID` int(12) NOT NULL,  `PublishDate` date NOT NULL) ENGINE=InnoDB DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_pinglun
CREATE TABLE `fangbao_pinglun` (  `PingLunID` int(12) NOT NULL AUTO_INCREMENT,  `ID` int(12) NOT NULL,  `PingLunName` varchar(100) NOT NULL,  `PingLunContent` text NOT NULL,  `VerOrderID` int(12) NOT NULL,  `Time` date NOT NULL,  `IsShow` int(1) NOT NULL DEFAULT '0',  PRIMARY KEY (`PingLunID`)) ENGINE=InnoDB DEFAULT CHARSET=gbk
DROP TABLE IF EXISTS fangbao_rect
CREATE TABLE `fangbao_rect` (  `ID` int(12) unsigned NOT NULL AUTO_INCREMENT,  `VerOrder` varchar(100) NOT NULL,  `PublishDate` date NOT NULL,  `PicFile` varchar(255) DEFAULT NULL,  `PdfFile` varchar(255) DEFAULT NULL,  `BanMian` varchar(255) NOT NULL,  `Rect` longtext,  `IsFrist` int(1) DEFAULT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=gbk
